import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import * as firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from './firebase.config';

firebase.initializeApp(firebaseConfig);

function App() {
  const provider = new firebase.auth.GoogleAuthProvider();
  const [user,setUser] = useState({
    isSignedIn : false,
    name : '',
    email : '',
    photo : ''
  })
  const signInHandler=()=>{
    firebase.auth().signInWithPopup(provider)
    .then(res => {
      const {displayName, email, photoURL} = res.user;
      setUser({
        isSignedIn : true,
        name : displayName,
        email : email,
        photo : photoURL
      })
    })
    .catch(err => {
      document.getElementById('error').innerText = err;
    }) 
  }
  const signOutHandler = () => {
    firebase.auth().signOut()
    .then(res=> {
      setUser({
        isSignedIn : false,
        name : '',
        email : '',
        photo : ''
      })
    })
    .catch( err => {
      document.getElementById('error').innerText = err;

    })
  }
  return (
    <div className="App">
      <h1>Firebase Authentication</h1>
      {
        user.isSignedIn ? <button className="btn btn-danger" onClick={signOutHandler}>Sign Out</button>:
                          <button className="btn btn-success" onClick={signInHandler}>Sign In</button>
      }
      <p id="error">  </p>
     {
        user.isSignedIn && 
        <div className="card">
          <img className="card-img-top img-fluid" width="30%" src={user.photo} alt=""/>
          <p>Welcome {user.name}</p>
          <p>your Email: {user.email}</p>
        </div>
     }
    </div>
  );
}

export default App;
