import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../node_modules/font-awesome/css/font-awesome.min.css';
import * as firebase from "firebase/app";
import "firebase/auth";
import firebaseConfig from './firebase.config';

firebase.initializeApp(firebaseConfig);

function App() {
  const provider = new firebase.auth.GoogleAuthProvider();
  const [user, setUser] = useState({
    isSignedIn: false,
    name: '',
    email: '',
    photo: ''
  })
  const signInHandler = () => {
    firebase.auth().signInWithPopup(provider)
      .then(res => {
        const { displayName, email, photoURL } = res.user;
        setUser({
          isSignedIn: true,
          name: displayName,
          email: email,
          photo: photoURL
        })
      })
      .catch(err => {
        document.getElementById('error').innerText = err;
      })
  }
  const signOutHandler = () => {
    firebase.auth().signOut()
      .then(res => {
        setUser({
          isSignedIn: false,
          name: '',
          email: '',
          photo: ''
        })
      })
      .catch(err => {
        document.getElementById('error').innerText = err;

      })
  }
  return (
    <div className="App">
     <div className="col-4 mx-auto">
     <h1>Firebase Authentication</h1>
      {
        !user.isSignedIn &&
        <button className="btn" style={{ backgroundColor: '#92342e', color: '#fff' }} onClick={signInHandler}><i className="fa fa-google"></i> Continue with Gmail</button>
      }
      <p id="error">  </p>
      {
        user.isSignedIn &&
        <div className="card text-center mx-auto bg-dark text-light" style={{ width: '250px', marginTop: '5%' }}>
          <img className="card-img-top rounded-circle mx-auto" src={user.photo} style={{ maxWidth: '170px', marginTop: '-80px' }} alt="" />

          <div className="card-header">
            Welcome {user.name}
          </div>
          <div className="card-body">
            <p className="card-text">Email: {user.email}</p>
          </div>
          <div className="card-footer">
            <button className="btn btn-danger" onClick={signOutHandler}>Sign Out</button>
          </div>
        </div>
      }
     <p className="divider-text my-4">
        <span className="bg-light px-5">OR</span>
      </p>
     <form>
        <div className="form-group input-group">
          <div className="input-group-prepend">
            <span className="input-group-text"> <i className="fa fa-envelope"></i> </span>
          </div>
          <input name="" className="form-control" placeholder="Email address" type="email" />
        </div>
        <div className="form-group input-group">
          <div className="input-group-prepend">
            <span className="input-group-text"> <i className="fa fa-lock"></i> </span>
          </div>
          <input className="form-control" placeholder="Enter password" type="password" />
        </div>
        <div className="form-group">
          <button type="submit" className="btn btn-primary btn-block"> Create Account  </button>
        </div>
        <p className="text-center">Have an account? <button className="btn btn-primary">Log In</button> </p>
      </form>

     </div>
    </div>
  );
}

export default App;
